import React, { Component } from "react";
import { Controlled as CodeMirror } from "react-codemirror2";

import "codemirror/lib/codemirror.css";
import "codemirror/theme/material.css";

import "codemirror/mode/htmlmixed/htmlmixed";
import "codemirror/mode/css/css";
import "codemirror/mode/javascript/javascript";

import "./App.css";

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      id: "",
      html: "",
      showHTML : false,
      showCSS : false,
      showJS : false
    };
    this.hideComponent = this.hideComponent.bind(this);
  }

  hideComponent(name) {
    switch (name) {
      case "showHTML":
        this.setState({ showHTML: true, showCSS: false, showJS: false });
        break;
      case "showCSS":
        this.setState({ showCSS: true, showJS: false, showHTML: false });
        break;
      case "showJS":
        this.setState({ showJS: true, showHTML: false, showCSS: false });
        break;
    }
  }

  componentDidUpdate() {
    this.runCode();
  }

  runCode = () => {
    const { html, css, js } = this.state;

    const iframe = this.refs.iframe;
    const document = iframe.contentDocument;
    const documentContents = `
      <!DOCTYPE html>
      <html lang="en">
      <head>
        <meta charset="UTF-8">
        <meta name="viewport" content="width=device-width, initial-scale=1.0">
        <meta http-equiv="X-UA-Compatible" content="ie=edge">
        <title>Document</title>
        <style>
          ${css}
        </style>
      </head>
      <body>
        ${html}

        <script type="text/javascript">
          ${js}
        </script>
      </body>
      </html>
    `;

    document.open();
    document.write(documentContents);
    document.close();
  };

  render() {
    const { html, css, js } = this.state;
    const { showHTML, showCSS, showJS } = this.state;
    const codeMirrorOptions = {
      theme: "material",
      lineNumbers: true,
      scrollbarStyle: null,
      lineWrapping: true
    };

    return (
      <div className="App">
        <section className="files">
          <div className="title-header">EDITOR TYPE</div>
          <div>
        <table>
          <tr>
            <td>
              <button>Submit</button>
            </td>
          </tr>
          <tr>
            <td>
              <button onClick={() => this.hideComponent("showHTML")}>
                Open HTML Editor
              </button>
            </td>
            <td>
              <button onClick={() => this.hideComponent("showCSS")}>
                Open CSS Editor
              </button>
            </td>
            <td>
              <button onClick={() => this.hideComponent("showJS")}>
                Open JS Editor
              </button>
            </td>
          </tr>
        </table>
      </div>
        </section>

        <section className="playground">
          <div className="code-editor html-code">
            <div className="title-header">EDITOR</div>
              {showHTML && (
                <CodeMirror
                  value={html}
                  options={{
                    mode: 'htmlmixed',
                    ...codeMirrorOptions,
                  }}
                  onBeforeChange={(editor, data, html) => {
                    this.setState({ html });
                  }}
                />
              )}
              {showCSS && (
                <CodeMirror
                  value={css}
                  options={{
                    mode: 'css',
                    ...codeMirrorOptions,
                  }}
                  onBeforeChange={(editor, data, css) => {
                    this.setState({ css });
                  }}
                />
              )}
              {showJS && (
                <CodeMirror
                  value={js}
                  options={{
                    mode: 'javascript',
                    ...codeMirrorOptions,
                  }}
                  onBeforeChange={(editor, data, js) => {
                    this.setState({ js });
                  }}
                />
              )}
            </div>
        </section>

        <section className="result">
          <div className="title-header">RESULT</div>
          <iframe title="result" className="iframe" ref="iframe" />
        </section>
      </div>
    );
  }
}

export default App;
